#!/usr/bin/python3 # -*- coding: utf-8 -*- import json import requests import math key = &#39;your key here&#39; # 这里填写你的百度开放平台的key x_pi = 3.14159265358979324 * 3000.0 / 180.0 pi = 3.1415926535897932384626 # π a = 6378245.0 # 长半轴 ee = 0.00669342162296594323 # 扁率 def geocode(address): &#34;&#34;&#34; 利用百度geocoding服务解析地址获取位置坐标 :param address:需要解析的地址 :return: &#34;&#34;&#34; geocoding = {&#39;s&#39;: &#39;rsv3&#39;, &#39;key&#39;: key, &#39;city&#39;: &#39;全国&#39;, &#39;address&#39;: address} res = requests.get( &#34;http://restapi.amap.com/v3/geocode/geo&#34;, params=geocoding) if res.status_code == 200: json = res.json() status = json.get(&#39;status&#39;) count = json.get(&#39;count&#39;) if status == &#39;1&#39; and int(count) &gt;= 1: geocodes = json.get(&#39;geocodes&#39;)[0] lng = float(geocodes.get(&#39;location&#39;).split(&#39;,&#39;)[0]) lat = float(geocodes.get(&#39;location&#39;).split(&#39;,&#39;)[1]) return [lng, lat] else: return None else: return None def gcj02tobd09(lng, lat): &#34;&#34;&#34; 火星坐标系(GCJ-02)转百度坐标系(BD-09) 谷歌、高德——&gt;百度 :param lng:火星坐标经度 :param lat:火星坐标纬度 :return: &#34;&#34;&#34; z = math.sqrt(lng * lng + lat * lat) + 0.00002 * math.sin(lat * x_pi) theta = math.atan2(lat, lng) + 0.000003 * math.cos(lng * x_pi) bd_lng = z * math.cos(theta) + 0.0065 bd_lat = z * math.sin(theta) + 0.006 return [bd_lng, bd_lat] def bd09togcj02(bd_lon, bd_lat): &#34;&#34;&#34; 百度坐标系(BD-09)转火星坐标系(GCJ-02) 百度——&gt;谷歌、高德 :param bd_lat:百度坐标纬度 :param bd_lon:百度坐标经度 :return:转换后的坐标列表形式 &#34;&#34;&#34; x = bd_lon - 0.0065 y = bd_lat - 0.006 z = math.sqrt(x * x + y * y) - 0.00002 * math.sin(y * x_pi) theta = math.atan2(y, x) - 0.000003 * math.cos(x * x_pi) gg_lng = z * math.cos(theta) gg_lat = z * math.sin(theta) return [gg_lng, gg_lat] def wgs84togcj02(lng, lat): &#34;&#34;&#34; WGS84转GCJ02(火星坐标系) :param lng:WGS84坐标系的经度 :param lat:WGS84坐标系的纬度 :return: &#34;&#34;&#34; if out_of_china(lng, lat): # 判断是否在国内 return lng, lat dlat = transformlat(lng - 105.0, lat - 35.0) dlng = transformlng(lng - 105.0, lat - 35.0) radlat = lat / 180.0 * pi magic = math.sin(radlat) magic = 1 - ee * magic * magic sqrtmagic = math.sqrt(magic) dlat = (dlat * 180.0) / ((a * (1 - ee)) / (magic * sqrtmagic) * pi) dlng = (dlng * 180.0) / (a / sqrtmagic * math.cos(radlat) * pi) mglat = lat + dlat mglng = lng + dlng return [mglng, mglat] def gcj02towgs84(lng, lat): &#34;&#34;&#34; GCJ02(火星坐标系)转GPS84 :param lng:火星坐标系的经度 :param lat:火星坐标系纬度 :return: &#34;&#34;&#34; if out_of_china(lng, lat): return lng, lat dlat = transformlat(lng - 105.0, lat - 35.0) dlng = transformlng(lng - 105.0, lat - 35.0) radlat = lat / 180.0 * pi magic = math.sin(radlat) magic = 1 - ee * magic * magic sqrtmagic = math.sqrt(magic) dlat = (dlat * 180.0) / ((a * (1 - ee)) / (magic * sqrtmagic) * pi) dlng = (dlng * 180.0) / (a / sqrtmagic * math.cos(radlat) * pi) mglat = lat + dlat mglng = lng + dlng return [lng * 2 - mglng, lat * 2 - mglat] def transformlat(lng, lat): ret = -100.0 + 2.0 * lng + 3.0 * lat + 0.2 * lat * lat + \ 0.1 * lng * lat + 0.2 * math.sqrt(math.fabs(lng)) ret += (20.0 * math.sin(6.0 * lng * pi) + 20.0 * math.sin(2.0 * lng * pi)) * 2.0 / 3.0 ret += (20.0 * math.sin(lat * pi) + 40.0 * math.sin(lat / 3.0 * pi)) * 2.0 / 3.0 ret += (160.0 * math.sin(lat / 12.0 * pi) + 320 * math.sin(lat * pi / 30.0)) * 2.0 / 3.0 return ret def transformlng(lng, lat): ret = 300.0 + lng + 2.0 * lat + 0.1 * lng * lng + \ 0.1 * lng * lat + 0.1 * math.sqrt(math.fabs(lng)) ret += (20.0 * math.sin(6.0 * lng * pi) + 20.0 * math.sin(2.0 * lng * pi)) * 2.0 / 3.0 ret += (20.0 * math.sin(lng * pi) + 40.0 * math.sin(lng / 3.0 * pi)) * 2.0 / 3.0 ret += (150.0 * math.sin(lng / 12.0 * pi) + 300.0 * math.sin(lng / 30.0 * pi)) * 2.0 / 3.0 return ret def out_of_china(lng, lat): &#34;&#34;&#34; 判断是否在国内，不在国内不做偏移 :param lng: :param lat: :return: &#34;&#34;&#34; if lng &lt; 72.004 or lng &gt; 137.8347: return True if lat &lt; 0.8293 or lat &gt; 55.8271: return True return False if __name__ == &#39;__main__&#39;: lng = 1